    private void obtenir(javax.swing.JButton temp){
        if (azero) {
            saisie="";
            azero=false;
            mv=true;
            operateur=new String[40];
            nbre=new double[40];
            io=0; 
        }
        saisie+=temp.getText();
        String s="";
        if (io>0) {
            for (int i = 0; i < io; i++) {
                s+=Double.toString(nbre[i])+operateur[i];
            }
        }
        afficheResultat.setText(s+saisie);
    }
    private void traiteOperateur(String o){
        if(!"".equals(saisie)){
            azero=false;
            nbre[io]=Double.parseDouble(saisie);
            operateur[io]=o;
            io++;
            String s="";
            if (io>0) {
                for (int i = 0; i < io; i++) {
                    s+=Double.toString(nbre[i])+operateur[i];
                }
            }
            afficheResultat.setText(s);
        }
        saisie="";
    }
