    private void bcActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bcActionPerformed
        // TODO add your handling code here:
        String v=afficheResultat.getText();
        if(".".equals(v) ){
            afficheResultat.setText("Erreur");
            azero=true;
            mv=true;   
        }else{
            if("".equals(v) ){
                v="0";
             }
            double val= Double.parseDouble(v);
            val*=val;
            afficheResultat.setText(Double.toString(val));
            saisie=Double.toString(val);
            azero=true;
        }
        
    }//GEN-LAST:event_bcActionPerformed

    private void beActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_beActionPerformed
        // TODO add your handling code here:
        String v=afficheResultat.getText();
        if(!"".equals(v) ){
            if(io>0){
                double resultat=nbre[0];
                nbre[io]=Double.parseDouble(saisie);
                for (int i = 1; i <=io; i++) {
                    switch (operateur[i-1]) {
                        case "+":
                            resultat+=nbre[i];
                            break;
                        case "-":
                            resultat-=nbre[i];
                            break;
                        case "*":
                            resultat*=nbre[i]; 
                            break;
                        case "/":
                            resultat/=nbre[i];
                            break;
                    }
                }
                afficheResultat.setText(Double.toString(resultat));
                saisie=Double.toString(resultat);
                operateur=new String[40];
                nbre=new double [40];
                io=0;
                azero=true;
            }
        }
    }//GEN-LAST:event_beActionPerformed

